import React, { useState, useEffect } from 'react';
import './App.css';
import axios, { AxiosResponse } from "axios";
import List from './List';
import Pagination from './pagination';
import IndividualData from './Individualdata';


const username = 'nsragvi@gmail.com';
const password = 'Anasuya@97';
const encodedBase64Token = Buffer.from(`${username}:${password}`).toString('base64');
const authorization = `Basic ${encodedBase64Token}`;
//let individualData = false;

function App2() {
  //const count = 0;
  const [responseData, setResponseData] = React.useState([]);
  const [loading,setLoading] = useState(false);
  //as we are looking to start with the first page
  const [ currentPage, setCurrentPage] = useState(1);
  //setting the number of lists per page
  const [postsperPage , setpostsPerPage] = useState(25);
  const [individualData , setindividualData] = useState(false);
  const [indvidual_index , setindvidual_index] = useState(-1);

  //change page
  const paginate = (pageNumber : number) => setCurrentPage(pageNumber);

  //get data
  const getdata = (index : number) =>{
    console.log("here");
    setindividualData(true);
    setindvidual_index(index);
  }
  useEffect(() => {

    //we can set the loading to true
    setLoading(true);
    axios.get('http://localhost:9000/test', {
      headers: {
        Authorization: authorization,
      },
    })

      .then((response: AxiosResponse) => {
        console.log("Response receieved"+response.data);
        return response.data;
      })
      .then(odata =>{
        //trying passing the array here itself
        setResponseData(odata);
        //console.log(responseData);
      });

     setLoading(false);    

     //setting the dependency array to empty
  },[]);

  const indexoflast = currentPage * postsperPage ;

  const indexoffirst = indexoflast - postsperPage;
  let currentPosts : string[] =  [];
  if(responseData!=null){
    currentPosts = responseData.slice(indexoffirst,indexoflast);
  }
  return (
    <div className="container mt-5">
      <h1 className= "text-primary mb-3"> 
        Ticket Requests
      </h1>
    {!individualData && responseData && <List requests={currentPosts} loading ={loading} getdata ={getdata}></List>} 
    {!individualData && responseData && <Pagination postsperPage={postsperPage} totalPosts={responseData.length} paginate={paginate}></Pagination>}
    {individualData && responseData && <IndividualData index ={indvidual_index} home = {getback}></IndividualData>} 
    </div>
  );
}

export default App2;
